import Main from "./Main";
import Footer from "./Footer";
import Header from "./Header";
import ImagePopup from "./PopupImage";
import React from "react";
import api from "../utils/api";
import { CurrentUserContext } from '../context/CurrentUserContext'
import EditProfilePopup from './EditProfilePopup'
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import { Route, Switch, useHistory } from "react-router-dom";
import Login from "./Login";
import Register from "./Register"
import ProtectedRoute from "./ProtectedRoute";
import * as mestoAuth from "../mestoAuth"
import InfoToolTip from "./InfoToolTip";

function App() {

  const [cards, setCards] = React.useState([]);
  const history = useHistory();
  const [currentUser, setCurrentUser] = React.useState({});
  const [loggedIn, setLoggedIn] = React.useState(false)
  const [userData, setUserData] = React.useState('')

  function handleUpdateUser({name, about}){

      api.addUserId({name, about}).then((res) => {
        setCurrentUser({
          name: res.name, about:res.about, avatar: res.avatar})
      }).catch((res)=>{
        console.log(res)
      }).finally(()=>{
        closeAllPopups()
      });
    };


    function handleUpdateAvatar({avatar}){

      api.addAvatarId({avatar}).then((res) => {
        setCurrentUser({
   avatar: res.avatar, name: res.name, about:res.about})
      }).catch((res)=>{
        console.log(res)
      }).finally(()=>{
        closeAllPopups()
      });
    };


  React.useEffect(() => {
    api.getUserId().then((res) => {
      if (!loggedIn) {
        return;
      }
      setCurrentUser({
        name: res.name,
        about: res.about,
        avatar: res.avatar,
        _id: res._id
      })

    }).catch((res)=>{
      console.log(res)
    });
  }, [loggedIn]);




  React.useEffect(() => {
    api.getInitialCards().then((item) => {
      if (!loggedIn) {
        return;
      }
      setCards(item);
      console.log(item)
    }).catch((res)=>{
      console.log(res)
    });
  }, [loggedIn]);

function handleAddPlaceSubmit({name, link}){
  api.addCards({name, link}).then(newCard => {
    setCards([newCard, ...cards]);
  }).catch((res)=>{
    console.log(res)
  }).finally(()=>{
    closeAllPopups()
  });;
};


function handleDeleteCard({cardId}) {

  api.deleteCard(cardId).then(() => {
    const newCards = cards.filter(card => card._id !== cardId);
    setCards(newCards);
  });
}


  function handleCardLike({likes, cardId}) {
    const isLiked = likes.some(i => i === currentUser._id);

    api.likeCard(cardId, !isLiked).then((newCard) => {
      console.log(isLiked)
      const newCards = cards.map(c => c._id === cardId ? newCard : c);
      setCards(newCards);
    });
}


console.log(cards)
